                $params["{post_url_relative}"] = "topic.php?fid=" . xrawurlencode($post_data["forum_id"]) . "&tid=" . xrawurlencode($post_data["topic_id"]) . "&setdone=1&msg=" . xrawurlencode($post_data["post_id"]);
                
