                "interface_language" => $dbw->field_by_name("interface_language"),
                "ignores_all_guests" => $dbw->field_by_name("ignore_guests_whitelist"),
                "words" => preg_split("/[\r\n]+/", $dbw->field_by_name("words_to_notify"), -1, PREG_SPLIT_NO_EMPTY)
            );
