            $params["{citated_post_url_relative}"] = "topic.php?fid=" . xrawurlencode($sinfo["citated_forum"]) . "&tid=" . xrawurlencode($sinfo["citated_topic"]) . "&msg=" . xrawurlencode($sinfo["citated_post"]);
            
