    //---------------------------------------------------------------
    function is_ip_whitelisted($ip, &$rules, &$matched_rule)
    {
        $ip = trim($ip);
        
        if (empty($ip)) {
            return false;
        }
        
        $matched_rule = $ip;
        
        foreach ($rules as $rule) {
            if (strpos($ip, $rule) === 0) {
                $matched_rule = $rule;
                return true;
            }
        }
        
        return false;
    } // is_ip_whitelisted
} // class ForumManager
